import React, { useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, Button, Card, ListGroup } from 'react-bootstrap';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [message, setMessage] = useState('');
  const [products, setProducts] = useState([]);
  const [skinTone, setSkinTone] = useState(null);

  // Handle file selection and preview
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    setImagePreview(URL.createObjectURL(file));
  };

  // Handle file upload and receive product suggestions
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!selectedFile) {
      setMessage("Please select a file first.");
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://127.0.0.1:8000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMessage(response.data.message);
      setProducts(response.data.products);
      setSkinTone(response.data.skinTone); // Assuming the backend returns skin tone
    } catch (error) {
      console.error("There was an error uploading the file!", error);
      setMessage("Error uploading file. Please try again.");
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="shadow-sm p-4">
            <h2 className="text-center mb-4">Upload Your Photo</h2>
            <Form onSubmit={handleUpload}>
              <Form.Group controlId="formFile" className="mb-3">
                <Form.Label>Select a photo to upload</Form.Label>
                <Form.Control type="file" onChange={handleFileChange} />
              </Form.Group>
              <Button variant="primary" type="submit" className="w-100 mb-4">
                Upload
              </Button>
            </Form>

            {imagePreview && (
              <Card className="mb-3">
                <Card.Img variant="top" src={imagePreview} alt="Uploaded Preview" />
              </Card>
            )}

            {message && (
              <div className="alert alert-info mt-3">
                {message}
              </div>
            )}

            {skinTone && (
              <div className="mt-4">
                <h4>Detected Skin Tone: <span style={{ backgroundColor: skinTone, padding: '0.5rem', borderRadius: '0.5rem' }}>{skinTone}</span></h4>
              </div>
            )}

            {products.length > 0 && (
              <div className="mt-4">
                <h4>Suggested Outfits:</h4>
                <ListGroup variant="flush">
                  {products.map((product, index) => (
                    <ListGroup.Item key={index}>{product}</ListGroup.Item>
                  ))}
                </ListGroup>
              </div>
            )}
          </Card>
        </Col>

        <Col md={6} className="d-flex flex-column align-items-center">
          <h4 className="mb-4">Color Palette and Outfit Suggestions</h4>
          <div className="d-flex justify-content-between">
            <div className="color-swatch" style={{ backgroundColor: '#D4A373' }}></div>
            <div className="color-swatch" style={{ backgroundColor: '#FFB7A2' }}></div>
            <div className="color-swatch" style={{ backgroundColor: '#FF7E67' }}></div>
          </div>
          <div className="outfit-suggestions mt-4">
            <img src="/path-to-outfit-1.jpg" alt="Outfit 1" className="outfit-img" />
            <img src="/path-to-outfit-2.jpg" alt="Outfit 2" className="outfit-img" />
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
